mapper.select:
select e_user_paper.*,
sys_user.name
from e_user_paper
left join sys_user on e_user_paper.user_id=sys_user.id


        and id = #{userPaper.id}


        and user_id = #{userPaper.userId}


        and exam_id = #{userPaper.examId}


        and status LIKE CONCAT('%',#{userPaper.status  },'%')


        and enter_time = #{userPaper.enterTime}


        and finish_time = #{userPaper.finishTime}


        and score = #{userPaper.score}


        and create_time = #{userPaper.createTime}


        and update_time = #{userPaper.updateTime}


        and create_by LIKE CONCAT('%',#{userPaper.createBy  },'%')


        and update_by LIKE CONCAT('%',#{userPaper.updateBy  },'%')


order by id desc

SELECT e_exam_info.exam_name as exam_name, sys_user.name as name, e_user_paper.status, e_user_paper.enter_time, e_user_paper.finish_time, e_user_paper.score, basic_department.name as dept_name FROM e_user_paper LEFT JOIN sys_user on e_user_paper.user_id = sys_user.id LEFT JOIN e_exam_info on e_user_paper.exam_id = e_exam_info.id LEFT JOIN basic_department on sys_user.department_id = basic_department.id
e_user_paper.exam_id = #{examId}
and e_user_paper.status = #{status}
order by finish_time

SELECT e_user_question.*, e_exam_question.score as total_score, e_question.type , e_question.content, e_question_option.id as option_id, e_question_option.content as option_content, e_question_option.is_true as option_is_true FROM e_user_question LEFT JOIN e_exam_question ON e_exam_question.question_id = e_user_question.question_id left join e_question on e_question.id = e_user_question.question_id left join e_question_option on e_question_option.question_id=e_user_question.question_id
e_user_question.paper_id = #{paperId}
and e_question.type = #{type}
